A = [3 1; 5 2]# Pegar dimensão de uma matrizsize(A)# Pegar tamanho/quantidade de elementos de uma matrizlength(A)# Carregar dados de um arquivo em uma variávelload('features.dat')# Mostra variáveis dos escopo atualwho# Mostra variáveis do escopo atual com mais detalheswhos# Deletar variávelclear features.dat# Salvar/criar um arquivosave features.dat v# Salavar como um texto ASCIIsave features.dat -v --ascii# Deletar todas variáveisclear# Pegar todas linhas de uma matriz (e a segunda coluna)A(:,2)# Pegar todas colunas de uma matriz (e a primeira linha)A(1,:)# Pegar conjunto de linhas (da primeira a terceira) e todas colunasA([1 3], :)# Fazer append de uma nova coluna à direitaA = [A, [10; 1; 10]]# A sintaxe para fazer append de linhas também é bem intuitivaA = [A; 1 2 3] # Colocando um nova linha no final# Coloca todos elementos de uma matriz em um único vetorA(:)